import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock, faTrash } from "@fortawesome/free-solid-svg-icons";
import services from "../Services/dataServices";
import "../Styles/home.css";
const Completed = () => {
  const [taskdata, setTaskData] = useState([]);

  useEffect(() => {
    services
      .getAll()
      .then((response) => {
        setTaskData(response);
      })
      .catch((err) => {
        console.log(`Error while fetching data from DB :${err}`);
      });
  }, []);

  const handleSubmission = (taskId) => {
    // do nothing
  };

  const handleDelete = (taskId) => {
    services
      .deleteData(taskId)
      .then(() => {
        setTaskData((prevTaskData) =>
          prevTaskData.filter((task) => task.id !== taskId)
        );
      })
      .catch((error) => {
        console.error("Error deleting task:", error);
      });
  };

  return (
    <div className="container-fluid vh-100 bg-dark text-white">
      <div className="row justify-content-center align-items-start">
        <div className="col-12 col-md-8">
          <h1 className="text-center mt-3">COMPLETED TASKS</h1>
          <table className="table table-bordered table-hover table-striped">
            <thead>
              <tr>
                <th>Tasks</th>
                <th>Due-Date</th>
                <th>Submissions</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {taskdata
                .filter((task) => task.taccomplished)
                .map((task) => {
                  const date = new Date(task.date);
                  const formattedDate = date.toLocaleDateString();
                  const truncatedTaskName = task.tname.slice(0, 20);

                  return (
                    <tr key={task.id}>
                      <td className="align-middle" style={{ width: "50%" }}>
                        {truncatedTaskName}
                      </td>
                      <td className="align-middle" style={{ width: "15%" }}>
                        {formattedDate}
                      </td>
                      <td className="align-middle">
                        <button
                          type="submit"
                          onClick={() => handleSubmission(task.id)}
                          disabled={task.taccomplished}
                          className="btn btn-success btn-sm"
                        >
                          {task.taccomplished
                            ? "Completed"
                            : "Mark as Submitted"}
                        </button>
                      </td>
                      <td className="align-middle">
                        <button
                          type="button"
                          onClick={() => handleDelete(task.id)}
                          className="btn btn-danger btn-sm"
                        >
                          <FontAwesomeIcon icon={faTrash} />
                        </button>
                      </td>
                    </tr>
                  );
                })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Completed;
