import React, { useState, useEffect } from "react";
import { Container, Table } from "react-bootstrap";
import services from "../Services/dataServices";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock, faTrash } from "@fortawesome/free-solid-svg-icons";

const Completed = () => {
  const [taskdata, setTaskData] = useState([]);
  const [loadingTasks, setLoadingTasks] = useState([]); // State to manage loading status of each task

  useEffect(() => {
    console.log("effect");
    services
      .getAll()
      .then((response) => {
        console.log(response);
        setTaskData(response);
      })
      .catch((err) => {
        console.log(`Error while fetching data from DB :${err}`);
      });
  }, []);

  const handleSubmission = (taskId) => {
    // do nothing
  };

  const handleDelete = (taskId) => {
    services
      .deleteData(taskId)
      .then(() => {
        setTaskData((prevTaskData) =>
          prevTaskData.filter((task) => task.id !== taskId)
        );
      })
      .catch((error) => {
        console.error("Error deleting task:", error);
      });
  };

  const isTaskLoading = (taskId) => {
    return loadingTasks.includes(taskId);
  };

  return (
    <Container className="my-5">
      <h1 className="text-center mb-4">COMPLETED TASKS</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Tasks</th>
            <th>Due-Date</th>
            <th>Submissions</th>
            <th>Delete</th> {/* Add a new table header for the delete button */}
          </tr>
        </thead>
        <tbody>
          {taskdata
            .filter((task) => task.taccomplished)
            .map((task) => {
              const date = new Date(task.date);
              const formattedDate = date.toLocaleDateString();
              const truncatedTaskName = task.tname.slice(0, 20);

              return (
                <tr key={task.id}>
                  <td>{truncatedTaskName}</td>
                  <td>{formattedDate}</td>
                  <td>
                    <button
                      type="submit"
                      onClick={() => handleSubmission(task.id)}
                      disabled={task.taccomplished}
                      className="btn btn-primary"
                    >
                      {isTaskLoading(task.id) ? (
                        <>
                          <span>+10 P</span>
                          <FontAwesomeIcon icon={faClock} spin />
                          <span>INTS</span>
                        </>
                      ) : task.taccomplished ? (
                        "Completed"
                      ) : (
                        "Mark as Submitted"
                      )}
                    </button>
                  </td>
                  <td>
                    {" "}
                    {/* Add a new table cell for the delete button */}
                    <button
                      type="button"
                      onClick={() => handleDelete(task.id)}
                      className="btn btn-danger"
                    >
                      <FontAwesomeIcon icon={faTrash} />{" "}
                      {/* Add the delete icon */}
                    </button>
                  </td>
                </tr>
              );
            })}
        </tbody>
      </Table>
    </Container>
  );
};

export default Completed;
