import React, { useState, useEffect } from 'react';
import "../Styles/home.css";
import services from "../Services/dataServices";
import { faClock } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';


const Scheduled = () => {
  const [taskdata, setTaskData] = useState([]);
  const [loadingTasks, setLoadingTasks] = useState([]); // State to manage loading status of each task
  const currentDate = new Date();
  currentDate.setHours(0, 0, 0, 0); // Set time to 00:00:00
  
  useEffect(() => {
    // console.log('effect');
    services
      .getAll()
      .then(response => {
        // console.log(response);
        setTaskData(response);
      })
      .catch(err => {
        console.log(`Error while fetching data from DB :${err}`);
      });
  }, []);

  const handleSubmission = (taskId) => {
    // Update loading status for the specific task
    setLoadingTasks(prevState => [...prevState, taskId]);
  
    // Find the task to be updated
    const taskToUpdate = taskdata.find(task => task.id === taskId);
  
    // Update the task's taccomplished value
    const updatedTask = { ...taskToUpdate, taccomplished: !taskToUpdate.taccomplished };
  
    // Simulate a 2-second delay before sending the update request
    setTimeout(() => {
      services
        .updateData(taskId, updatedTask)
        .then(response => {
          setTaskData(prevTaskData =>
            prevTaskData.map(task => (task.id === taskId ? updatedTask : task))
          );
  
          // Update loading status for the specific task after completion
          setLoadingTasks(prevState => prevState.filter(id => id !== taskId));
        })
        .catch(error => {
          console.error('Error updating task:', error);
          // Update loading status for the specific task after failure
          setLoadingTasks(prevState => prevState.filter(id => id !== taskId));
        });
    }, 2000); // 2-second delay
  };
  

  const isTaskLoading = (taskId) => {
    return loadingTasks.includes(taskId);
  };

  const handleDelete = (taskId) => {
    services
      .deleteData(taskId)
      .then(() => {
        setTaskData(prevTaskData => prevTaskData.filter(task => task.id !== taskId));
      })
      .catch(error => {
        console.error('Error deleting task:', error);
      });
  };

  return (
    <div className="home-container">
      <div className="home-content">
        <h1>ALL TASKS</h1>
        <table className="task-list">
          <thead>
            <tr>
              <th>Tasks</th>
              <th>Due-Date</th>
              <th>Submissions</th>
            </tr>
          </thead>
          <tbody>
            {taskdata
            .filter(task => !(new Date(task.date).setHours(0, 0, 0, 0) === currentDate.getTime()))
            .map((task) => {
              const date = new Date(task.date);
              const formattedDate = date.toLocaleDateString();
              const truncatedTaskName = task.tname.slice(0, 20); // Take only the first 20 characters of the task name
              
              return (
                <tr key={task.id} className="task-item">
                  <td className="task-name">{truncatedTaskName}</td>
                  <td className="task-date">{formattedDate}</td>
                  <td className="submission">
                          <button
                            type="submit"
                            onClick={() => handleSubmission(task.id)}
                            disabled={task.taccomplished} // Disable the button if the task is already completed
                          >
                            {isTaskLoading(task.id) ? (
                              <>
                                <span>+10   P</span>
                                <FontAwesomeIcon icon={faClock} spin />
                                <span>INTS</span>
                              </>
                            ) : (task.taccomplished ? 'Completed' : 'Mark as Submitted')}
                          </button>
                        </td>
                        <td className="delete"> {/* Add a new table cell for the delete button */}
                      <button type="button" onClick={() => handleDelete(task.id)}>
                        <FontAwesomeIcon icon={faTrash} /> {/* Add the delete icon */}
                      </button>
                    </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Scheduled;
