import React, { useState, useEffect } from 'react'
import "../Styles/home.css";
import  services from "../Services/dataServices";

const Home = () => {
  const [taskdata, setTaskData] = useState([]);
  useEffect(() => {
    console.log('effect')
    services
      .getAll()
      .then(response => {
        //console.log('promise fulfilled')
        console.log(response)
        setTaskData(response)
      })
      .catch(err=>{
        console.log(`Error while fetching data from DB :${err}`)
    })
  }, [])
  return ( 
    <div className="home-container">
    <div className="home-content">
      <h1>You are in home page.</h1>
      <table className="task-list">
        <thead>
          <tr>
            <th>Task</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {taskdata.map((task) => {
            const date = new Date(task.date);
            const formattedDate = date.toLocaleDateString();
            return (
              <tr key={task.id} className="task-item">
                <td className="task-name">{task.tname}</td>
                <td className="task-date">{formattedDate}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  </div>
  );
};

export default Home;
