import React, { useState, useEffect } from 'react';
import "../Styles/home.css";
import services from "../Services/dataServices";
import { faClock } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Completed = () => {
  const [taskdata, setTaskData] = useState([]);
  const [loadingTasks, setLoadingTasks] = useState([]); // State to manage loading status of each task

  useEffect(() => {
    console.log('effect');
    services
      .getAll()
      .then(response => {
        console.log(response);
        setTaskData(response);
      })
      .catch(err => {
        console.log(`Error while fetching data from DB :${err}`);
      });
  }, []);

  const handleSubmission = (taskId) => {
        // write code for deleting only
  };
  

  const isTaskLoading = (taskId) => {
    return loadingTasks.includes(taskId);
  };

  return (
    <div className="home-container">
      <div className="home-content">
        <h1>ALL TASKS</h1>
        <table className="task-list">
          <thead>
            <tr>
              <th>Tasks</th>
              <th>Due-Date</th>
              <th>Submissions</th>
            </tr>
          </thead>
          <tbody>
  {taskdata
    .filter(task => task.taccomplished) // Filter only completed tasks
    .map((task) => {
      const date = new Date(task.date);
      const formattedDate = date.toLocaleDateString();
      const truncatedTaskName = task.tname.slice(0, 20); // Take only the first 20 characters of the task name

      return (
        <tr key={task.id} className="task-item">
          <td className="task-name">{truncatedTaskName}</td>
          <td className="task-date">{formattedDate}</td>
          <td className="submission">
            <button
              type="submit"
              onClick={() => handleSubmission(task.id)}
              disabled={task.taccomplished} // Disable the button if the task is already completed
            >
              {isTaskLoading(task.id) ? (
                <>
                  <span>+10   P</span>
                  <FontAwesomeIcon icon={faClock} spin />
                  <span>INTS</span>
                </>
              ) : (task.taccomplished ? 'Completed' : 'Mark as Submitted')}
            </button>
          </td>
        </tr>
      );
    })}
</tbody>

        </table>
      </div>
    </div>
  );
};

export default Completed;
